						else if( _status == '9' )
							msg = "알수없는 오류입니다.";
						else if( _status == '99' )
							msg = "저장경로 네트워크 설정이 잘못되었습니다.";
						else
							msg = "이미지 변환에 실패하였습니다.";
						$("#span"+_fileSn).html(
								"<a href=\"javascript:convert('"+_atchFileId+"','"+_fileSn+"');\">" +
								"이미지변환하기" +
								"</a>");
					}
					$("#loading").hide();
					alert(msg);
				}
			);
	}

