	$('.popup .btn_x').click(function(){
		$('body').css({"overflow": ""});
		$('.popup').addClass('hide');
	});
	$('.popup .jq_x').click(function(){
		$('body').css({"overflow": ""});
		$('.popup').addClass('hide');
	});
	/* LAYER */
	$('.jq_layer').click(function(){
		$('.layer').hide();
		$(this).next('.layer').show();
	});
	$('.layer .btn_x').click(function(){
		$('.layer').hide();
	});
	/* BTN_TOP */
	$('.btn_top').click(function () {
		$('body,html').animate({
			scrollTop: 0
		}, 500);
		return false;
	});
