전체질문지 자료 function delFile(atchFileId, fileSn, bbsId){
		if (confirm('삭제하시겠습니까?'))
		{
		$.getJSON(

				"/portal/cmm/fms/deleteFileInfs.json",
				{atchFileId : atchFileId, fileSn : fileSn, bbsId : bbsId},
				function(data)
				{
					var jdata = data.resultCode;
		            if( jdata == 'success' ) {
		            	alert("성공적으로 삭제하였습니다.");
		            	$("#realfile_"+fileSn).html("");
		            	file_number-=1;

		            }
		            else alert("삭제에 실패하였습니다.");
				}
			);
		}
	}

	function convert(_atchFileId, _fileSn)
	{
		$("#loading").show();
		$.getJSON(
				"/cmm/dozen/convert.json",
				{atchFileId : _atchFileId, fileSn : _fileSn},
				function(data){
					var _result = data.result;
					var _status = data.status;
					var msg;
					if( _result && _status == '0' ){
						msg = "성공적으로 이미지 변경되었습니다.";
						$("#span"+_fileSn).html(
								"<a href='/cmm/dozen/view.do?atchFileId="+_atchFileId+"&fileSn="+_fileSn+"' target='_blank'>" +
								"빠른보기" +
								"</a>");
					}
					else{
						if( _status == '1' )
							msg = "원본파일 경로가 잘못되었습니다.";
						else if( _status == '2' )
							msg = "파일에 암호가 있어 변환에 실패하였습니다.";
						else if( _status == '3' )
							msg = "파일이 존재하지 않습니다.";
						else if( _status == '4' )
							msg = "적합한 변환기를 찾지 못하였습니다.";
						else if( _status == '5' )
							msg = "잘못된 요청입니다. 연동규격 위반입니다.";
						else if( _status == '6' )
							msg = "PDF 생성에 실패하였습니다. 요청하신 경로에 동일한이름의 PDF가 존재합니다";
						else if( _status == '7' )
							msg = "변환 요청 제한시간이 만료되었습니다. (기본값 : 120초)";
						else if( _status == '8' )
							msg = "라이선스 파일이 존재하지 않거나 만료되었습니다.";
						else if( _status == '9' )
							msg = "알수없는 오류입니다.";
						else if( _status == '99' )
							msg = "저장경로 네트워크 설정이 잘못되었습니다.";
						else
							msg = "이미지 변환에 실패하였습니다.";
						$("#span"+_fileSn).html(
								"<a href=\"javascript:convert('"+_atchFileId+"','"+_fileSn+"');\">" +
								"이미지변환하기" +
								"</a>");
					}
					$("#loading").hide();
					alert(msg);
				}
			);
	}

	function view(atchFileId, fileSn, bbsId, bbsKey){
		location.href = '/portal/cmm/fms/FileDown.do?atchFileId='+atchFileId+'&fileSn='+fileSn+'&bbsId='+bbsId+'&bbsKey='+bbsKey;
	}

































