	.wrap{padding:0px !important;}
	.content {
	-webkit-print-color-adjust: exact !important;
	visibility: visible;
	height: 100%;
	width: 100% !important;
/*	position: relative; */
	top: 0;
	left: 0;
	margin: 0;
	padding: 15px;
	font-size: 14px;
	line-height: 18px;
	overflow: visible;
	}
}



