								"</a>");
					}
					else{
						if( _status == '1' )
							msg = "원본파일 경로가 잘못되었습니다.";
						else if( _status == '2' )
							msg = "파일에 암호가 있어 변환에 실패하였습니다.";
						else if( _status == '3' )
							msg = "파일이 존재하지 않습니다.";
						else if( _status == '4' )
							msg = "적합한 변환기를 찾지 못하였습니다.";
						else if( _status == '5' )
							msg = "잘못된 요청입니다. 연동규격 위반입니다.";
						else if( _status == '6' )
							msg = "PDF 생성에 실패하였습니다. 요청하신 경로에 동일한이름의 PDF가 존재합니다";
