/*===========================================================================================
세션 타이머 관련 함수
===========================================================================================*/
var fvSesSecond  = 10*60;
var timerchecker = null;

$(document).ready(function(){
	/* 세션 타이머에 사용 된 Login 유무 체크  */
	var strValue = '';

	if( strValue != null && strValue != '' )
	{
		initTimer();
	}
});

//모바일 에이전트 구분
var isMobile = {
        Android: function () {
                 return navigator.userAgent.match(/Android/i) == null ? false : true;
        },
        BlackBerry: function () {
                 return navigator.userAgent.match(/BlackBerry/i) == null ? false : true;
        },
        IOS: function () {
                 return navigator.userAgent.match(/iPhone|iPad|iPod/i) == null ? false : true;
        },
        Opera: function () {
                 return navigator.userAgent.match(/Opera Mini/i) == null ? false : true;
        },
        Windows: function () {
                 return navigator.userAgent.match(/IEMobile/i) == null ? false : true;
        },
        any: function () {
                 return (isMobile.Android() || isMobile.BlackBerry() || isMobile.IOS() || isMobile.Opera() || isMobile.Windows());
        }
};

$(document).on('keyup','.numbersOnly',function(){

	var inputval = $(this).val();
	if(inputval != '0'){
		inputval = inputval.replace(/(^0+)/, "");
	}
	$(this).val(inputval.replace(/[^0-9]/gi,''));

});

function logout(){
	var msg = "로그아웃 하시겠습니까?";
	var con = confirm(msg);

	if(con){
		clearTimer();
		location.href = "/portal/member/logout.do?menuNo=200473";
	}
}

/*===========================================================================================
세션 타이머 관련 함수
WorkArea Body에 essionTimer INCLUDE 해줘야 함
===========================================================================================*/
function addTimer()
{
	fvSesSecond = 10*60;
	clearTimeout(timerchecker);
	initTimer();
}

function clearTimer()
{
	fvSesSecond = 10*60;
	clearTimeout(timerchecker);
}

function initTimer()
{
	var rMinute = parseInt(fvSesSecond / 60);
	var rSecond = fvSesSecond % 60;

	if(fvSesSecond > 59){
		$("#timeoutArea").text( (rMinute < 10 ? "0"+rMinute:rMinute) +":"+ (rSecond<10 ? "0"+rSecond:rSecond));
		$("#addTimeoutArea").text( (rSecond<10 ? "0"+rSecond:rSecond));

		fvSesSecond--;
		timerchecker = setTimeout("initTimer()",1000);
	}
	else
	{
		$('body').css({"overflow": "hidden"});
		$('#addTimer').removeClass('hide');

		$("#timeoutArea").text( (rMinute < 10 ? "0"+rMinute:rMinute) +":"+ (rSecond<10 ? "0"+rSecond:rSecond));
		$("#addTimeoutArea").text( (rSecond<10 ? "0"+rSecond:rSecond));

		fvSesSecond--;
		timerchecker = setTimeout("initTimer()",1000);

		if( fvSesSecond < 0 )
		{
			stopTimePop();
		}
	}
}

function addTimePop()
{
	addTimer();
	$('body').css({"overflow": ""});
	$('#addTimer').addClass('hide');
}

function stopTimePop()
{
	clearTimer();
	$('body').css({"overflow": ""});
	$('#addTimer').addClass('hide');

	location.href = "/portal/member/logout.do?menuNo=200473";
