function dayCount(arr){
	if (arr != ""){
		var jbAry = arr.split("/");
		var a = {};
		for (var i = 0; i < jbAry.length; i++) {
			if (typeof a[jbAry[i]] == 'undefined') {
	            a[jbAry[i].substring(0,10)] = 1;
	        }
	    }

		jbAry.length = 0;
	    for (var i in a) {
	    	jbAry[jbAry.length] = i;
	    }
	    $('#totalDay').text(jbAry.length +"일");
	}
}

