function jf_serveyTypeChoice(opt){
	tabNumber = opt;
	if(opt == '1'){
		$('.set1').show();
		$('.set2').hide();
		$('.set3').hide();
		$('.set4').hide();
		$('.set5').hide();
	}else if(opt == '2'){
		$('.set1').hide();
		$('.set2').show();
		$('.set3').hide();
		$('.set4').hide();
		$('.set5').hide();
	}else if(opt == '3'){
		$('.set1').hide();
		$('.set2').hide();
		$('.set3').show();
		$('.set4').hide();
		$('.set5').hide();
	}else if(opt == '4'){
		$('.set1').hide();
		$('.set2').hide();
		$('.set3').hide();
		$('.set4').show();
		$('.set5').hide();
	}else if(opt == '5'){
		$('.set1').hide();
		$('.set2').hide();
		$('.set3').hide();
		$('.set4').hide();
		$('.set5').show();
	}
}







