$(document).ready(function() {

	/* TABLE SCROLL */
	$(function(){
        $(window).resize(function () {
            if ($(this).width() < 960) {
                $(".scroll").css({"width": ($(this).width()-20) +"px"});
            } else {
                $(".scroll").css({"width": 100 +"%"});
            }
        }).resize();
    });
	/* TOP FIX */
    $(function(){
        $(window).scroll(function () {
            if ($(this).scrollTop() > 100) {
                $('header').addClass('fix');
				$('.quick').addClass('fix');
            } else {
                $('header').removeClass('fix');
				$('.quick').removeClass('fix');
            }
        });
    });
	/* GNB */
	$('.btn_gnb').click(function(){
		$('.gnb').animate({left: '0'});
	});
	$('.btn_gnb_x').click(function(){
		$('.gnb').animate({left: '-300px'});
	});
	/* DEPTH */
	if(depth>0){
		$(".lnb > ul > li:eq("+(depth-1)+")").addClass('on');
	};
	/* SLIDE */
	$('.slide').bxSlider({
		mode: 'horizontal',
		speed: 1500,
		pager: true,
		autoControls: true,
		auto: true,
		autoHover: true,
	});
	$('.slide_logo').bxSlider({
		mode: 'horizontal',
		speed: 3000,
		minSlides : 2,
		maxSlides : 5,
		slideWidth : 160,
		slideMargin : 10,
		pager: false,
		auto: true,
		autoHover: true,
	});
	/* GRID */
	var colCount = $($(".grid .row.th")[0]).find(".col").length;
	var trCount = $(".grid .row.tr").length;

	for(var j = 0 ; j <trCount ; j++){
		for(var i = 0; i < colCount; i++){
			var gridText = $(".grid .row.th .col").eq(i).text();
			$($($(".grid .row.tr")[j]).find(".col")[i]).find("i").text(gridText);
		}
	};
	/* SITE */
	$('.site').click(function(){
		if ($(this).hasClass('on')) {
			$(this).removeClass('on');
		} else {
			$(this).addClass('on');
		}
	});
	$('.site').mouseleave(function(){
		$(this).removeClass('on');
	});
	/* SCROLL */
	$('.scroll').hover(function(){
		$(this).addClass('on');
	});
	$('.scroll').scroll(function(){
		$(this).addClass('on');
	});
	/* TOGGLE */
	$('.jq-toggle').click(function(){
		if ($(this).hasClass('on')) {
			$(this).removeClass('on');
		} else {
			$(this).addClass('on');
		}
	});
	/* TAP */
	$('.tapt').click(function(){
		$(this).siblings('.tapt').removeClass('on');
		$(this).addClass('on');
		$(this).parent('.tap').siblings('.tapview').children('.tapc').removeClass('on');
		$(this).parent('.tap').siblings('.tapview').children('.tapc').eq($(this).index()).addClass('on');
		var cateText = $(this).text();
		$(this).parents().siblings('label').text(cateText);
		$(this).parents().siblings('label').removeClass('on');
	});
	/* STEP */
	$('.step.jq > .tep').click(function(){
		$('.tep').removeClass('ok');
		$(this).siblings('.tep').removeClass('on');
		$(this).prev('.tep').addClass('ok');
		$(this).addClass('on');
	});
	/* SELECT */
	$('.select').click(function(){
		if ($(this).hasClass('on')) {
                $(this).removeClass('on');
            } else {
				$('.select').removeClass('on');
                $(this).addClass('on');
            }
	});
	$('select').change(function(){
        var selectText = $(this).children('option:selected').text();
        $(this).siblings('label').text(selectText);
    });
	/* POPUP */
	$('.jq_popup').click(function(){
		$('body').css({"overflow": "hidden"});
		$('.popup').removeClass('hide');
	});
	$('.popup .btn_x').click(function(){
		$('body').css({"overflow": ""});
		$('.popup').addClass('hide');
	});
	$('.popup .jq_x').click(function(){
		$('body').css({"overflow": ""});
		$('.popup').addClass('hide');
	});
	/* LAYER */
	$('.jq_layer').click(function(){
		$('.layer').hide();
		$(this).next('.layer').show();
	});
	$('.layer .btn_x').click(function(){
		$('.layer').hide();
	});
	/* BTN_TOP */
	$('.btn_top').click(function () {
		$('body,html').animate({
			scrollTop: 0
		}, 500);
		return false;
	});
})
